import { useEffect, useState } from "react";
import styles from "./Main.module.scss";
import ImageItem from "../ImageItem/ImageItem";
import { dataImages } from "../../data/imageData";


const Main = () => {
    const data = JSON.parse(localStorage.getItem("imgId")) || dataImages;
    const [images, setImages] = useState(data);
    const [deletedImages, setDeletedImages] = useState([]);
    const [currentDate, setCurrentDate] = useState("");
  
    const formatCurrentDate = () => {
      const now = new Date();
      const formattedDate = `${now.getDate()}.${now.getMonth() + 1}.${now.getFullYear()} ${now.getHours()}:${now.getMinutes()}`;
      setCurrentDate(formattedDate);
    };
  
    const intervalId = setInterval(formatCurrentDate, 60000);
  
    useEffect(() => {
      formatCurrentDate();
  
      return () => {
        clearInterval(intervalId);
      };
    }, []);
  
    useEffect(() => {
      // Сохранение удаленных изображений в localStorage
      localStorage.setItem("deletedImages", JSON.stringify(deletedImages));
    }, [deletedImages]);
  
    const handleDelete = (id) => {
      // Находим изображение по id и удаляем его
      const imageToDelete = images.find((img) => img.id === id);
      if (imageToDelete) {
        const updatedImages = images.filter((img) => img.id !== id);
        setImages(updatedImages);
  
        // Добавляем удаленное изображение в список удаленных
        setDeletedImages([...deletedImages, imageToDelete]);
      }
    };
  
    const handleRestore = () => {
      // Восстанавливаем удаленные изображения
      setImages([...images, ...deletedImages]);
      setDeletedImages([]);
    };
  
    return (
      <main className={styles.container}>
        <div className={styles.info}>
          <div>Current date : {currentDate}</div>
          <div>Count images : {images.length}</div>
        </div>
        <div className={styles.images_container}>
          {images.map((el) => (
            <div key={el.id} className={styles.imageContainer}>
              <ImageItem imgItem={el.img} id={el.id} />
              <button onClick={() => handleDelete(el.id)} className={styles.deleteButton}>
                &#10006;
              </button>
            </div>
          ))}
        </div>
        {deletedImages.length > 0 && (
          <button onClick={handleRestore} className={styles.restoreButton}>
            Восстановить
          </button>
        )}
      </main>
    );
  };
  

  
  export default Main;

