import Modal from "../Modal/Modal";
import { useState } from "react";
import btn from "../../img_task/remove.png";
import styles from "./ImageItem.module.scss";

const ImageItem = ({ imgItem }) => {
  const [active, setEctive] = useState(false);
  // const addToImageTolist =(e) =>{
  
  //   localStorage.setItem("imgId",imgItem);

  // }
  return (
    <div onClick={() => setEctive(true)} className={styles.item}>
      <img className={styles.image} src={imgItem} alt={imgItem.id} />
      {/* <div className={styles.btn}><img  src={btn} alt="remove" /></div> */}
      <img  className="" src={btn} alt="remove" />
      <div>
      <Modal img={imgItem} active={active} setEctive={() => setEctive(false)} />
      </div>
      
    </div>
  );
};

export default ImageItem;
