import cn from "classnames";
import styles from "./Modal.module.scss";

const Modal = ({active,img,setEctive}) => {
    return(
        <div className={active ? cn(styles.modal, styles.active) : styles.modal}>
            <div>
                <div>
                <img className={styles.image} src={img} alt={img.id} />
                </div>
                
                {/* <img className={styles.btn} src="" alt="close" /> */}
                <button onClick={setEctive}>close</button>
                
            </div>

        </div>
    )
}

export default Modal;
