import { useEffect, useState } from "react";
import styles from "./Main.module.scss";
import ImageItem from "../ImageItem/ImageItem";
import { dataImages } from "../../data/imageData";


const Main = () => {
    const [imeges, setImeges] = useState([]);
    const [currentDate, setCurrentDate] = useState("");
  
    const filterImages = (array) => {
      if (localStorage.getItem("itemId")) {
        const storageIds = JSON.parse(localStorage.getItem("itemId"));
        
        // Фильтруем изображения, исключая те, чьи id есть в localStorage
        return array.filter((image) => !storageIds.includes(image.id));
      }
      
      return array; // Если в localStorage нет данных, возвращаем исходный массив
    };
  
    const formatCurrentDate = () => {
      const now = new Date();
      const formattedDate = `${now.getDate()}.${now.getMonth() + 1}.${now.getFullYear()} ${now.getHours()}:${now.getMinutes()}`;
      setCurrentDate(formattedDate);
    };
  
    const intervalId = setInterval(formatCurrentDate, 60000);
  
    useEffect(() => {
      setImeges(filterImages(dataImages));
      formatCurrentDate();
  
      return () => {
        clearInterval(intervalId);
      };
    }, [intervalId]);
  
    return (
      <main className={styles.container}>
        <div className={styles.info}>
          <div>Current date : {currentDate}</div>
          <div>Count images : {imeges.length}</div>
        </div>
        <div className={styles.images_container}>
          {imeges.map((el) => {
            return <ImageItem key={el.id} imgItem={el.img} id={el.id} />;
          })}
        </div>
      </main>
    );
  };

 export default Main;

