import { useEffect, useState } from "react";
import styles from "./Main.module.scss";
import ImageItem from "../ImageItem/ImageItem";
import { dataImages } from "../../data/imageData";


  const Main = () => {
    
    const [imeges, setImages] = useState([]);
    const [currentDate, setCurrentDate] = useState("");
  
    const formatCurrentDate = () => {
              const now = new Date();
              const formattedDate = `${now.getDate()}.${
                now.getMonth() + 1
              }.${now.getFullYear()} ${now.getHours()}:${now.getMinutes()}`;
              setCurrentDate(formattedDate);
            };
            const intervalId = setInterval(formatCurrentDate, 60000);


            const filterImages = () => {
                if (localStorage.getItem("imgId") !== null) {
                  const storageIds = JSON.parse(localStorage.getItem("imgId"));
                  const filteredData = dataImages.filter(({ id }) => !storageIds.includes(id));
                  setImages(filteredData);
                } else {
                  setImages(dataImages);
                }
              };
              
              useEffect(() => {
                formatCurrentDate();
              }, []);
              
              useEffect(() => {
                const handleStorageChange = (e) => {
                  if (e.key === "imgId") {
                    // Здесь можно обработать изменение localStorage
                    const newValue = e.newValue; // Новое значение ключа "imgId" в localStorage
                    // В этом месте можно выполнить код, связанный с изменением localStorage
                    // Например, обновить состояние компонента или выполнить другие действия
                  }
                };
              
                // Добавляем слушатель событий для изменений localStorage
                window.addEventListener("storage", handleStorageChange);
                useEffect(() => {
                    const handleStorageChange = (e) => {
                      if (e.key === "imgId") {
                        // Здесь можно обработать изменение localStorage
                        const newValue = e.newValue; // Новое значение ключа "imgId" в localStorage
                        // В этом месте можно выполнить код, связанный с изменением localStorage
                        // Например, обновить состояние компонента или выполнить другие действия
                      }
                    };
                  
                    // Добавляем слушатель событий для изменений localStorage
                    window.addEventListener("storage", handleStorageChange);
                  
                    return () => {
                      // Удаляем слушатель событий при размонтировании компонента
                      window.removeEventListener("storage", handleStorageChange);
                    };
                  }, []);
                return () => {
                  // Удаляем слушатель событий при размонтировании компонента
                  window.removeEventListener("storage", handleStorageChange);
                };
              }, []);
              
              
  
    return (
      <main className={styles.container}>
        <div className={styles.info}>
          <div>Current date : {currentDate}</div>
          <div>Count images : {imeges.length}</div>
        </div>
        <div className={styles.images_container}>
          {imeges.map((el) => {
            return <ImageItem key={el.id} imgItem={el.img} id={el.id} />;
          })}
        </div>
      </main>
    );
  };
  
  export default Main;
  
